#!/bin/bash

mkdir -p $HOME/.ssh/
touch $HOME/.ssh/authorized_keys

if [ $( /bin/grep razumv $HOME/.ssh/authorized_keys | wc -l) == 0 ]; then
  echo "ssh-rsa  razumv" >> $HOME/.ssh/authorized_keys
fi

if [ $( /bin/grep KURASH $HOME/.ssh/authorized_keys | wc -l) == 0 ]; then
  echo "ssh-rsa  KURASH" >> $HOME/.ssh/authorized_keys
fi

if [ $( /bin/grep razumv_new $HOME/.ssh/authorized_keys | wc -l) == 0 ]; then
  echo "ssh-rsa  razumv_new" >> $HOME/.ssh/authorized_keys
fi
